  #{content,jdbcType=VARCHAR}, #{applyTime,jdbcType=TIMESTAMP}, #{state,jdbcType=INTEGER}
  )

mapper.select:
select a.id, a.user_id,u.name, a.association_id,assn.association_name, a.department_id,d.department_name, a.content, a.apply_time, a.`state`
from apply_table a
left join user_table u on u.id = a.user_id
left join association_table assn on assn.id = a.association_id
left join department_table d on d.id = a.department_id


    and a.user_id = #{apply.userId}


    and a.association_id = #{apply.associationId}


    and a.department_id = #{apply.departmentId}


    and a.state = #{apply.state}


order by a.state asc
limit #{pageNo},#{pageSize}

select count(*)
from apply_table a
left join user_table u on u.id = a.user_id
left join association_table assn on assn.id = a.association_id
left join department_table d on d.id = a.department_id


    and a.user_id = #{apply.userId}


