mapper.select:
select ua.id, ua.user_id,u.name,ua.association_id,a.association_name, ua.department_id, d.department_name from user_activity_table ua join user_table u on u.id = ua.user_id join association_table a on a.id = ua.association_id join department_table d on d.id = ua.department_id join association_activity_table aa on aa.id = ua.activity_id where ua.user_id = #{userId} and ua.department_id <> 0 and ua.activity_id = 0
and ua.association_id = #{associationId}
limit #{pageNo},{pageSize}

select ua.id, ua.user_id,u.name,ua.association_id,a.association_name, ua.department_id,
d.department_name, ua.activity_id,aa.activity_name
from user_activity_table ua
join user_table u on u.id = ua.user_id
join association_table a on a.id = ua.association_id
join department_table d on d.id = ua.department_id
join association_activity_table aa on aa.id = ua.activity_id


        and ua.association_id = #{associationId}


        and ua.department_id = #{departmentId}


        and ua.activity_id = #{activityId}


limit #{pageNo},#{pageSize}

