mapper.insert:

  SELECT LAST_INSERT_ID()

insert into user_table (identity_id, major, user_name, pwd,
  `name`, sex, age,
  tel_number, grade, user_img
  )
values (#{identityId,jdbcType=BIGINT}, #{major,jdbcType=VARCHAR}, #{userName,jdbcType=VARCHAR}, #{pwd,jdbcType=VARCHAR},
  #{name,jdbcType=VARCHAR}, #{sex,jdbcType=TINYINT}, #{age,jdbcType=INTEGER},
  #{telNumber,jdbcType=BIGINT}, #{grade,jdbcType=INTEGER}, #{userImg,jdbcType=VARCHAR}
  )

mapper.select:
select u.id,u.identity_id, i.identity_name, u.major, u.user_name, u.pwd, u.`name`,
u.sex, u.age, u.tel_number, u.grade, u.user_img, a.id as association_id ,a.association_name,
ua.job,d.id as department_id,d.department_name
from user_table u
left join `identity_table` i on i.id = u.identity_id
left join user_activity_table ua on u.id = ua.user_id
left join association_table a on a.id = ua.association_id
left join department_table d on d.association_id = a.id


     and  u.identity_id = #{identityId}


    and i.identity_name = #{identityName}


    and  a.id = #{associationId}


    and  d.id = #{departmentId}


    and  u.name = #{name}


    and  u.user_name = #{userName}


limit #{pageNo},#{pageSize}

select count(*)
from user_table u
left join `identity_table` i on i.id = u.identity_id
left join user_activity_table ua on u.id = ua.user_id
left join association_table a on a.id = ua.association_id
left join department_table d on d.association_id = a.id


          and  u.identity_id = #{identityId}


        and i.identity_name = #{identityName}


          and  a.id = #{associationId}


          and  d.id = #{departmentId}


          and  u.name = #{name}


          and  u.user_name = #{userName}

mapper.update:
update user_table
set pwd = #{pwd}
where pwd = #{oldPwd}
and user_name = #{newPwd}

update user_activity_table
set job = #{job}
where user_id = #{userId}
and association_id = #{associationId}
and department_id = 0
and activity_id = 0
